import { Text, View, StyleSheet , Button, Alert } from 'react-native';
import { FlatList } from 'react-native-gesture-handler';
import Dialog from 'react-native-dialog';


const tab_Course = [ 
    { id: "1", nom_produit: "Barre de Chocolat" }, 
    { id: "2", nom_produit: "Paquet de Lait" }, 
    { id: "3", nom_produit: "Shamppoing anti-pelliculaire" },
    { id: "4", nom_produit: "SÃ¨che-Cheveux" },
    { id: "5", nom_produit: "Coca-cola" },
    { id: "6", nom_produit: "Jus de pomme" },
    { id: "7", nom_produit: "Biscuits" },
    { id: "8", nom_produit: "Produit Linge" },
    { id: "9", nom_produit: "Orange" }
]

const [visible, setVisible] = useState(false);

  const showDialog = () => {
    setVisible(true);
  };

  const handleCancel = () => {
    setVisible(false);
  };

  const handleDelete = () => {
    setVisible(false);
};

class Liste_Course extends React.Component {


    render() {
        return (
            <View style={styles.container}>
                <Text style={{ fontSize: 30, marginBottom: 25 }}>La liste de course</Text> 
                <FlatList data={tab_Course} keyExtractor={(item) => item.id} renderItem={({item}) => <Text style={{ fontSize : 30 }}>{item.nom_produit}
                <Button 
                style={styles.button} 
                title="ðŸ›’" 
                onPress={this.popUp}/>
                </Text>
                }/>
                <Dialog.Container visible={visible}>
                <Dialog.Title>Account delete</Dialog.Title>
                <Dialog.Description>
                    Do you want to delete this account? You cannot undo this action.
                </Dialog.Description>
        <Dialog.Button label="Cancel" onPress={handleCancel} />
        <Dialog.Button label="Delete" onPress={handleDelete} />
      </Dialog.Container>
            </View>
        );
    }
}



const styles = StyleSheet.create({ 
    container: { 
        flex: 1, 
        paddingTop: 77, 
        alignItems: "center"
    },
    button : {
        flex: 1
    }
})

export default Liste_Course
