import React, { useState } from "react";
import { Text, View, StyleSheet , Button } from 'react-native';
import { FlatList, TouchableOpacity } from 'react-native-gesture-handler';
import DialogInput from 'react-native-dialog-input';



const tab_Course = [ 
    { id: "1", nom_produit: "Barre de Chocolat" }, 
    { id: "2", nom_produit: "Paquet de Lait" }, 
    { id: "3", nom_produit: "Shamppoing anti-pelliculaire" },
    { id: "4", nom_produit: "Sèche-Cheveux" },
    { id: "5", nom_produit: "Coca-cola" },
    { id: "6", nom_produit: "Jus de pomme" },
    { id: "7", nom_produit: "Biscuits" },
    { id: "8", nom_produit: "Produit Linge" },
    { id: "9", nom_produit: "Orange" }
]

class Liste_Course extends React.Component {
  constructor(props){
    super(props);
    state = {
      isDialogVisible: false
  }
}

  showDialog(isShow) {
   setState({isDialogVisible: isShow})
  }

  sendInput(InputText) {
    console.log("Vous avez acheté "+InputText+" quantité")
  }
    render() {
        return (
            <View style={styles.container}>
                <DialogInput isDialogVisible={this.state.isDialogVisible}
                  title={"DialogInput 1"}
                  message={"Message for DialogInput #1"}
                  hintInput ={"Quantité"}
                  submitInput={ (inputText) => {this.sendInput(inputText)} }
                  closeDialog={ () => {this.showDialog(false)}}>
                </DialogInput>

                <Text style={{ fontSize: 30, marginBottom: 25 }}>La liste de course</Text>
                <FlatList data={tab_Course} 
                  keyExtractor={(item) => item.id} 
                  renderItem={({item}) => <TouchableOpacity onPress={()=>{showDialog(true)}}><Text style={{ fontSize : 30 }}>{item.nom_produit}  🛒</Text>
                  </TouchableOpacity>
                }/>
            </View>
        );
  }
}

const styles = StyleSheet.create({ 
    container: { 
        flex: 1, 
        backgroundColor: "#fff",
        paddingTop: 42, 
        alignItems: "center",
        justifyContent: "center",
        position:"relative"
    }
})

export default Liste_Course;
