import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { Text, View, StyleSheet , Button, Alert } from 'react-native';
import { FlatList } from 'react-native-gesture-handler';
import Dialog, { DialogFooter, DialogButton } from 'react-native-popup-dialog';
//import { dialogue }  from './essai'


const tab_Course = [ 
    { id: "1", nom_produit: "Barre de Chocolat" }, 
    { id: "2", nom_produit: "Paquet de Lait" }, 
    { id: "3", nom_produit: "Shamppoing anti-pelliculaire" },
    { id: "4", nom_produit: "SÃ¨che-Cheveux" },
    { id: "5", nom_produit: "Coca-cola" },
    { id: "6", nom_produit: "Jus de pomme" },
    { id: "7", nom_produit: "Biscuits" },
    { id: "8", nom_produit: "Produit Linge" },
    { id: "9", nom_produit: "Orange" }
]


function dialogue() {

    let state = {
        visible: true
    }

    return (
    <View style={styles.container}>
        <Dialog
            visible={state.visible}
            footer={
            <DialogFooter>
                <DialogButton
                    text="OK"
                    onPress={() => {}}
                />
            </DialogFooter>
            }
        >
        </Dialog>
    </View>
    )
}

class Liste_Course extends React.Component {
    render() {
        return (
            <View style={styles.container}>
                <Text>La liste de course</Text> 
                <FlatList data={tab_Course}
                keyExtractor={(item) => item.id} 
                renderItem={({item}) => <Text>{item.nom_produit}<Button style={styles.button} title="ðŸ›’" onPress={dialogue()}/></Text>}/>
                <StatusBar style="auto" />
            </View>
        );
    }
}



const styles = StyleSheet.create({ 
    container: { 
        flex: 1, 
        paddingTop: 65, 
        alignItems: "center"
    },
    button : {
        flex: 1,
        color: "white",
        justifyContent: "space-between"
    }
})

export default Liste_Course
