import React, { useState } from "react";
import { Text, View, StyleSheet, Pressable } from 'react-native';
import { FlatList } from 'react-native-gesture-handler';
import Dialog from 'react-native-dialog';


const tab_Course = [ 
    { id: "1", nom_produit: "Barre de Chocolat" }, 
    { id: "2", nom_produit: "Paquet de Lait" }, 
    { id: "3", nom_produit: "Shamppoing anti-pelliculaire" },
    { id: "4", nom_produit: "Sèche-Cheveux" },
    { id: "5", nom_produit: "Coca-cola" },
    { id: "6", nom_produit: "Jus de pomme" },
    { id: "7", nom_produit: "Biscuits" },
    { id: "8", nom_produit: "Produit Linge" },
    { id: "9", nom_produit: "Orange" }
]

const Liste_Course = () => {
  const [visible, setVisible] = useState(false);

  const showDialog = () => {
    setVisible(true);
  };

  const handleDelete = () => {
    console.log("Là !")
    setVisible(false);
  };

  return (
   <View>
    <View style={styles.dialog}>
     <Dialog.Container visible={visible} 
       style={{position:"absolute"}}>
          <Dialog.Description>
              La quantité de ce produit acheté :
          </Dialog.Description>
          <Dialog.Input label=""
              placeholder="Quantité"
          />
          <Dialog.Button label="OK" onPress={handleDelete} />
      </Dialog.Container>
    </View> 
    <View style={styles.container}>        
      <Text style={{ fontSize: 30, marginBottom: 25 }}>La liste de course</Text>
      <FlatList data={tab_Course} 
      keyExtractor={(item) => item.id} 
      renderItem={({item}) => <Text style={{flex: 1, fontSize : 22, padding: 9}}>{item.nom_produit}
      <Pressable onPress={showDialog}><Text>  ✅   </Text>
      </Pressable></Text>
      }/>
    </View>
   </View>
        );
}


const styles = StyleSheet.create({ 
    container: { 
      flex: 1, 
      paddingTop: 90, 
      alignItems: "center",
      justifyContent: "center",
    },
    dialog: {
      position:"absolute",
    }
})

export default Liste_Course;
