import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { Text, View, StyleSheet , Button, Alert} from 'react-native';
import { FlatList } from 'react-native-gesture-handler';
import { Dialog } from './tabCourse'



const tab_Course = [ {   
        id:"1",
        nom_produit : "Barre de Chocolat",
    }, { 
        id: "2",
        nom_produit: "Paquet de Lait"
    }, {
        id: "3",
        nom_produit: "Shamppoing anti-pelliculaire"
    }
]

const confirmationAchat = () =>  {
        prompt(
        "Quantit√© achet√©",
      ); 

}

class Liste_Course extends React.Component {
    render() {
        return (
            <View style={styles.container}>
                <Text>La liste de course</Text> 
                <FlatList data={tab_Course}
                keyExtractor={(item) => item.id} 
                renderItem={({item}) => <Text>{item.nom_produit}<Button style={styles.button} title="üõí" onPress={confirmationAchat}/></Text>}/>
                <StatusBar style="auto" />
            </View>
        );
    }
}



const styles = StyleSheet.create({ 
    container: { 
        flex: 1, 
        paddingTop: 65, 
        alignItems: "center"
    },
    button : {
        flex: 1,
        color: "white",
        justifyContent: "space-between"
    }
})

export default Liste_Course
