import React from 'react';
import { Text, View, StyleSheet , Button } from 'react-native';
import { FlatList } from 'react-native-gesture-handler';
import Dialog, { DialogFooter, DialogButton, DialogContent } from 'react-native-popup-dialog';


const tab_Course = [ 
    { id: "1", nom_produit: "Barre de Chocolat" }, 
    { id: "2", nom_produit: "Paquet de Lait" }, 
    { id: "3", nom_produit: "Shamppoing anti-pelliculaire" },
    { id: "4", nom_produit: "Sèche-Cheveux" },
    { id: "5", nom_produit: "Coca-cola" },
    { id: "6", nom_produit: "Jus de pomme" },
    { id: "7", nom_produit: "Biscuits" },
    { id: "8", nom_produit: "Produit Linge" },
    { id: "9", nom_produit: "Orange" }
]

function confirmationQuantite() {
    const state = {
        visible : false
    }
    render
    return (
    <View>
        <Dialog
            visible={state.visible}
            footer={
                <DialogFooter>
                    <DialogButton
                    text="CANCEL"
                    onPress={() => {}}
                    />
                    <DialogButton
                    text="OK"
                    onPress={() => {}}
                    />
                </DialogFooter>
            }
        >
        <DialogContent>
        {}
        </DialogContent>
        </Dialog>
    </View>
    )
}

class Liste_Course extends React.Component {

    render() {
        return (
            <View style={styles.container}>
                <Text style={{ fontSize: 30, marginBottom: 25 }}>La liste de course</Text> 
                <FlatList data={tab_Course}
                keyExtractor={(item) => item.id} 
                renderItem={({item}) => <Text style={{ fontSize : 30 }}>{item.nom_produit}
                <Button 
                style={styles.button} 
                title="🛒" 
                onPress={confirmationQuantite}/>
                </Text>
                }
                />

            </View>
        );
    }
}



const styles = StyleSheet.create({ 
    container: { 
        flex: 1, 
        paddingTop: 77, 
        alignItems: "center"
    },
    button : {
        flex: 1
    }
})

export default Liste_Course
